-- database/init.sql

-- 1. CLEANUP (Reset the state)
DROP TABLE IF EXISTS adjudication_value_codes;
DROP TABLE IF EXISTS claim_adjustment_group_codes;
DROP TABLE IF EXISTS carc_codes;
DROP TABLE IF EXISTS rarc_codes;

-- 2. CREATE TABLES

-- Value Codes (What is this dollar amount?)
CREATE TABLE adjudication_value_codes (
    code VARCHAR(50) PRIMARY KEY,
    display VARCHAR(255),
    definition TEXT
);

-- Group Codes (Who is financially responsible?)
CREATE TABLE claim_adjustment_group_codes (
    code VARCHAR(10) PRIMARY KEY,
    description VARCHAR(255),
    responsibility TEXT,
    start_date DATE
);

-- CARC Codes (Why was it adjusted?)
CREATE TABLE carc_codes (
    code VARCHAR(10) PRIMARY KEY,
    description TEXT,
    action_hint TEXT,
    start_date DATE
);

-- RARC Codes (Specific details)
CREATE TABLE rarc_codes (
    code VARCHAR(10) PRIMARY KEY,
    description TEXT,
    start_date DATE
);

